import React from 'react'
import './Post.css';
import Avatar from "@material-ui/core/Avatar";

function Post({ username, caption, imageUrl }) {
    return (
        <div className="post">
            <div className="post__header">              
            <Avatar 
                className="post__avatar"
                alt='RafehQazi'
                src="/static/images/avatar/1.jpg"
                />
            <h3>{Username}</h3>
        </div>

            <img className="post__image" src="https://scontent.fbkk22-2.fna.fbcdn.net/v/t1.0-9/107842421_2667973340129504_2250674154371402536_n.jpg?_nc_cat=106&_nc_sid=09cbfe&_nc_eui2=AeGJPji405iMjnp4F_QeanVQcGcp-AGh_dtwZyn4AaH926HLM1Vnfnntvk8stWVovMD3h-YUlq-s83cM30LLNecW&_nc_ohc=xr9FYA4eqooAX-A7oFa&_nc_ht=scontent.fbkk22-2.fna&oh=e4e60cfdfee02379165550798a27c624&oe=5F7C5C57"/>

            <h4 className="post__text"><strong>{username}</strong></h4>
        </div>
    )
}

export default Post
